{"dist":{"index":"https://gw.alipayobjects.com/os/zeldatwa/e6126145-6904-4d06-a8a6-78ec69263bc0/dp-simple-draw-react/0.2.8/index.umd.js","indexMin":"https://gw.alipayobjects.com/os/zeldatwa/27550809-7b9f-46da-bee4-a48dde533dcd/dp-simple-draw-react/0.2.8/index.umd.min.js"},"configSchema":"Object(组件配置) {
  actTime(可抽奖时间段): Enum {
    notlimit(不限制),
    monthDay(每月一天),
    weekDay(每周一天)
  },
  monthConfig(几号抽奖): Number[ if: "$.actTime == 'monthDay'" ],
  weekConfig(周几抽奖): Number[ if: "$.actTime == 'weekDay'" ],
  showEvent(展示事件):String,
  activityConfig(活动相关配置): Array {
    activityId(活动id): String[regExp: "/^(CP)/g"],
    actBg(抽奖背景图): Image[maxWidth: 750],
    actWidth(抽奖背景图宽度): Number,
    actHeight(抽奖背景图高度): Number,
    hasConfig(已抽奖配置): Object {
      promptType(提示类型): Enum {
        modal(弹窗),
        toast(toast),
        link(跳转),
      },
      link(跳转链接): String,
      toastText(toast文案): String,
      bg(页面背景图): Image[maxWidth: 750, description: "尺寸和抽奖图保持一致"],
      image(点击弹窗图): Image,
      width(点击弹窗图图片宽度): Number[ if: "$.bg" ],
      height(点击弹窗图图片高度): Number[ if: "$.bg" ]
    },
    disableConfig(不可抽奖配置): Object {
      disableBg(不可抽奖背景图): Image[maxWidth: 750, description: "尺寸和抽奖图保持一致"],
      disableImage(不可抽奖弹窗设置): Object {
        image(错误提示弹窗图片): Image[maxWidth: 750],
        width(错误提示弹窗宽度): Number,
        height(错误提示弹窗高度): Number
      }
    },
    errorConfig(抽奖错误码配置,参考https://haitun2.alipay.com/doc/open_dolphin%2Ftemplate_develop%2Fmwi70y?current=uy9qdo): Array {
      errorCode(错误码): String,
      promptType(错误提示类型): Enum {
        modal(弹窗),
        toast(toast)
      },
      errorMsg(错误 toast 提示): String[ if: "$.promptType == 'toast'" ],
      errorImage(错误提示弹窗设置): Object[ if: "$.promptType == 'modal'" ] {
        image(错误提示弹窗图片): Image[maxWidth: 750],
        width(错误提示弹窗宽度): Number,
        height(错误提示弹窗高度): Number
      }
    },
    successConfig(抽奖成功配置): Object {
      promptType(提示类型): Enum {
        modal(弹窗),
        toast(toast)
      },
      toastText(toast文案): String,
      image(抽奖弹窗): Image[maxWidth: 750],
      width(抽奖弹窗宽度): Number[ if: "$.image" ],
      height(抽奖弹窗高度): Number[ if: "$.image" ]
    }
  },
  marginTop(上间距): Number
  marginBottom(下间距): Number
}
"}
